import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { useFavorites } from "../components/FavoritesContext";
import { useAuth0 } from "@auth0/auth0-react";
import { useCart } from "../components/CartContext";
import "./Recipes.css";

function FastRecipesColumn() {
  const [recipes, setRecipes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { favorites, addFavorite, removeFavorite } = useFavorites();
  const { cart, addToCart, removeFromCart } = useCart();
  const { isAuthenticated, loginWithRedirect } = useAuth0();
  const API_KEY = "540464a4610b4e4c9488d105323ad0af";
  const URL = `https://api.spoonacular.com/recipes/random?apiKey=${API_KEY}&number=25`;

  const navigate = useNavigate();

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();

        if (response.ok) {
          const filteredRecipes = data.recipes
            .filter((recipe) => recipe.readyInMinutes <= 30)
            .sort((a, b) => a.readyInMinutes - b.readyInMinutes);

          setRecipes(filteredRecipes);
        } else {
          throw new Error(data.message || "The recipes could not be loaded.");
        }
      } catch (error) {
        setError(error.message);
      } finally {
        setLoading(false);
      }
    };

    fetchRecipes();
  }, []);

  const handleRecipeClick = (recipeId) => {
    navigate(`/recipe/${recipeId}`, {
      state: { fromRecipeList: true },
    });
  };

  const toggleSaveRecipe = (recipe) => {
    if (!isAuthenticated) {
      loginWithRedirect();
      return;
    }

    if (favorites.some((fav) => fav.id === recipe.id)) {
      removeFavorite(recipe.id);
    } else {
      addFavorite(recipe);
    }
  };

  const addToCartHandler = (recipe, e) => {
    e.stopPropagation();
    if (!isAuthenticated) {
      loginWithRedirect();
      return;
    }

    if (cart.some((item) => item.id === recipe.id)) {
      removeFromCart(recipe.id);
    } else {
      addToCart(recipe);
    }
  };

  if (loading) {
    return (
      <div className="loading-container1">
        <p>Loading fast recipes...</p>
      </div>
    );
  }

  if (error) {
    return (
      <div className="error-container1">
        <p>{`Error: ${error}`}</p>
      </div>
    );
  }

  return (
    <section className="container-populars1">
      <div className="home-container-populars1">
        <div className="recipe-list1">
          {recipes.map((recipe) => (
            <div
              key={recipe.id}
              className="recipe-card1"
              onClick={() => handleRecipeClick(recipe.id)}
            >
              <div className="recipe-image1">
                <img
                  src={recipe.image}
                  alt={recipe.title}
                  className="recipe-image1"
                />
              </div>
              <div className="recipe-info1">
                <h3 className="carousel-title1">{recipe.title}</h3>
                <div className="carousel-meta1">
                  <span>
                    <i className="bx bx-time-five"></i> {recipe.readyInMinutes} min
                  </span>
                  <button
                    className={`save-btn1 ${
                      favorites.some((fav) => fav.id === recipe.id) ? "saved1" : ""
                    }`}
                    onClick={(e) => {
                      e.stopPropagation();
                      toggleSaveRecipe(recipe);
                    }}
                    aria-label={
                      favorites.some((fav) => fav.id === recipe.id)
                        ? "Remove from saved recipes"
                        : "Save recipe"
                    }
                  >
                    <i
                      className={
                        favorites.some((fav) => fav.id === recipe.id)
                          ? "bx bxs-heart"
                          : "bx bx-heart"
                      }
                    ></i>
                  </button>

                  <button
                    className="details-button1"
                    onClick={(e) => addToCartHandler(recipe, e)}
                    aria-label={
                      cart.some((item) => item.id === recipe.id)
                        ? "Remove from cart"
                        : "Add to cart"
                    }
                  >
                    <i
                      className={
                        cart.some((item) => item.id === recipe.id)
                          ? "bx bxs-plus"
                          : "bx bx-plus"
                      }
                    ></i>
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default FastRecipesColumn;
