import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

import "./Home.css";

function HomePopulars() {
  const [recipes, setRecipes] = useState([]);
  const { addToCart } = useCart();
  const API_KEY = "d0fba68ef5204602ac929844f28b7d5f";
  const maxTime = 30; // Tiempo máximo en minutos
  const URL = `https://api.spoonacular.com/recipes/complexSearch?apiKey=${API_KEY}&maxReadyTime=${maxTime}&number=10`;

  useEffect(() => {
    const fetchRecipes = async () => {
      try {
        const response = await fetch(URL);
        const data = await response.json();
        setRecipes(data.results); // Usar "results" para complexSearch
      } catch (error) {
        console.error("Error al cargar las recetas:", error);
      }
    };

    fetchRecipes();
  }, []);

  return (
    <section className="container-populars">
      <h1>Recetas en Menos de {maxTime} Minutos</h1>
      <div className="recipe-list">
        {recipes.map((recipe) => (
          <div key={recipe.id} className="recipe-card">
            <img src={recipe.image} alt={recipe.title} />
            <h3>{recipe.title}</h3>
            <p>Tiempo: {recipe.readyInMinutes} minutos</p>
            <Link to={`/recipe/${recipe.id}`}>Ver receta</Link>
            <button
              onClick={() => addToCart(recipe)}
              className="add-to-cart-button"
            >
              Añadir al carrito
            </button>
          </div>
        ))}
      </div>
    </section>
  );
}

export default HomePopulars;
